package week1.day1;

public class WaterBottle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int capacity = 130;
		System.out.println("The waterbottle Capacity1 is " +capacity+"ml");
		short capacity2 = 2000;
		System.out.println("The waterbottle capacity2 is " +capacity2);
		long phonenumber = 9234234234l;
		System.out.println("The phonenumber is "+phonenumber);
		float radius = 1.5f;
		System.out.println("Tje radius value is " +radius);
		double pieValue = 3.14444;
		System.out.println("The pie value is "+pieValue);
		String waterbottleColor = "pink";
		System.out.println("The waterbottle colour is "+waterbottleColor);
		char logo = 't';
		System.out.println("The logo type is "+logo);
		boolean isEmpty = false;
		System.out.println("The boolean value is "+isEmpty);
	}

}
